mapper.select:
SELECT id,CONCAT(parent_id,'') parent_id,name_en as name,name as display_name,org_code as short_name, manager,org_type,org_kind,responsibilities, (SELECT COUNT(1) FROM BMS_BASE_ORG f2 WHERE f2.parent_id = t.id) AS sub_count, (SELECT name FROM BMS_BASE_ORG f2 WHERE f2.id = t.parent_id) AS parent_name FROM BMS_BASE_ORG t WHERE t.deleted = 0
AND t.id=#{id}
AND t.parent_id=#{parent_id}
ORDER BY sort_number ASC

SELECT id,CONCAT(parent_id,'') parent_id,name_en as name,name as display_name, org_code as short_name,manager,org_type,org_kind,responsibilities, (SELECT name FROM BMS_BASE_ORG f2 WHERE f2.id = t.parent_id) AS parent_name from BMS_BASE_ORG t where 1=1 and t.deleted='0'
and t.id=#{id}
and t.parent_id=#{parent_id}
order by sort_number ASC

mapper.update:
update BMS_BASE_ORG set 

parent_id=#{parent_id},

org_type=#{org_type},org_kind=#{org_kind},name=#{name},name_en=#{name_en},org_code=#{org_code},manager=#{manager},responsibilities=#{responsibilities},deleted=#{deleted},editor_id=#{editor_id},edit_date=#{edit_date}  
where id=#{id}
