mapper.select:
SELECT f.production_qty,f.busnum_start,f.busnum_end,a.factory_name area,a.id AS 'factory_id',a.factory_name,f.edit_date,u.display_name as 'user_name', t.bus_type_code,o.*,CONCAT(o.order_name,t.bus_type_code,o.order_qty,'台') AS 'order_name_str',f.status, f.bus_number_start,(f.bus_number_start+f.busnum_end-f.busnum_start) bus_number_end FROM BMS_OR_ORDER o LEFT JOIN BMS_OR_FACTORY_ORDER f ON f.order_id = o.id LEFT JOIN BMS_BASE_FACTORY a ON f.factory_id = a.id LEFT JOIN BMS_BASE_BUS_TYPE t ON o.bus_type_id = t.id left join BMS_BASE_USER u on f.editor_id = u.id where 1=1
and o.order_no like CONCAT('%',#{orderNo},'%')
and o.order_name like CONCAT('%',#{orderName},'%')
and f.status = #{orderStatus}
and o.productive_year = #{actYear}
and f.factory_id = #{factory}
ORDER BY id DESC,d.busnum_start ASC
ORDER BY #{orderColumn} ASC
LIMIT #{start} ,#{length}

SELECT count(o.id) FROM BMS_OR_ORDER o
LEFT JOIN BMS_OR_FACTORY_ORDER f on o.id=f.order_id
LEFT JOIN BMS_BASE_FACTORY a ON f.factory_id = a.id 

	and o.order_no like CONCAT('%',#{orderNo},'%')   


	and o.order_name like CONCAT('%',#{orderName},'%')   


	and f.status = #{orderStatus} 


	and o.productive_year = #{actYear} 


	and f.factory_id = #{factory}

select d.id id,d.busnum_start busnum_start,d.busnum_end busnum_end,d.id factory_order_id,(d.busnum_end-d.busnum_start+1) production_qty,d.factory_id factory_id,o.order_no order_no,o.order_name order_name, o.order_code order_code,o.order_type order_type,o.order_qty order_qty,o.productive_year productive_year,t.bus_type_code bus_type_code, (SELECT ifnull(max(cast(reverse(substr(reverse(bus_number),1,instr(reverse(bus_number),'-')-1)) as signed)),0) FROM BMS_PLAN_BUS where order_id=d.order_id and factory_id=d.factory_id and factory_order_detail_id=d.id) maxbusnum, (SELECT ifnull(min(cast(reverse(substr(reverse(bus_number),1,instr(reverse(bus_number),'-')-1)) as signed)),0) FROM BMS_PLAN_BUS where order_id=d.order_id and factory_id=d.factory_id and factory_order_detail_id=d.id) minbusnum, d.order_id order_id,o.delivery_date delivery_date,f.factory_name factory_name,d.editor_id editor_id,d.edit_date edit_date,d.bus_number_count bus_number_count,d.bus_number_start bus_number_start from BMS_OR_FACTORY_ORDER d left join BMS_BASE_FACTORY f on f.id=d.factory_id left join BMS_OR_ORDER o on o.id=d.order_id left join BMS_BASE_BUS_TYPE t on o.bus_type_id = t.id where 1=1
and o.order_no like CONCAT('%',#{search_order_no},'%')
and o.order_name like CONCAT('%',#{search_order_name},'%')
and o.productive_year = #{search_productive_year}
and d.factory_id = #{search_factory}
and d.order_id = #{order_id}
ORDER BY d.busnum_start
LIMIT #{start} ,#{length}

mapper.insert:

	SELECT
	LAST_INSERT_ID()


insert into BMS_OR_FACTORY_ORDER(order_id,factory_id,production_qty,busnum_start,busnum_end,bus_number_start,bus_number_count,editor_id,edit_date) 
values(#{order_id},#{factory_id},#{production_qty},#{busnum_start},#{busnum_end},#{bus_number_start},
(SELECT count(bus_number) FROM  BMS_PLAN_BUS WHERE order_id = #{order_id}
AND welding_online_date IS NOT NULL 
AND RIGHT(bus_number,5) + 0 >= ${bus_number_start} 
AND RIGHT(bus_number,5) + 0 <= (${busnum_end} - ${busnum_start} + ${bus_number_start} + 0)
)
,#{editor_id},#{edit_date})
