and tf.order_no=#{order_no}
and tf.factory=#{factory}
and tf.workshop=#{workshop}
group by tf.tech_task_id,tf.order_no,tf.factory,tf.workshop UNION ALL SELECT tfp.tech_task_id,tfp.order_no,tfp.factory,tfp.workshop,sum(tfp.follow_num) follow_num FROM BMS_TECH_TASK_FOLLOW_PRE tfp WHERE tfp.confirmor_id>0
and tfp.order_no=#{order_no}
and tfp.factory=#{factory}
and tfp.workshop=#{workshop}
group by tfp.tech_task_id,tfp.order_no,tfp.factory,tfp.workshop) tmp WHERE tmp.tech_task_id=t.id and tmp.factory=d.factory and tmp.order_no=d.order_no) follow_list, (SELECT group_concat(tmp.workshop,':',tmp.ready_hour_total separator ',') ready_hour_list FROM( SELECT h.ecn_task_id tech_task_id,h.factory,h.workshop,ifnull(sum(h.work_hour),0)ready_hour_total FROM BMS_HR_STAFF_TECH_HOURS h WHERE h.status !='2'
and h.factory=#{factory}
and h.workshop=#{workshop}
group by h.ecn_task_id,h.factory,h.workshop) tmp WHERE tmp.tech_task_id=d.id and tmp.factory=d.factory ) ready_hour_list, d.factory_id,d.factory,d.tech_list,IFNULL(d.time_list,'') AS time_list,d.single_time_total,d.time_total,d.prod_factory_id,d.prod_factory,d.status_list FROM BMS_TECH_TASK t LEFT JOIN BMS_BASE_KEY k on t.tech_type=k.id and k.key_code='ECN_TYPE' LEFT JOIN BMS_BASE_KEY k1 on t.tech_order_type=k1.id and k1.key_code='ECN_CHANGE_TYPE' LEFT JOIN BMS_BASE_KEY k2 on t.duty_unit=k2.id and k2.key_code='ECN_DUTY_UNIT' LEFT JOIN BMS_TECH_TASK_DETAIL d ON t.id=d.tech_task_id LEFT JOIN BMS_OR_ORDER o ON o.order_no=d.order_no LEFT JOIN BMS_BASE_BUS_TYPE bt on bt.id=o.bus_type_id WHERE 1=1
and t.task_content like (concat('%',#{task_content},'%'))
and t.tech_order_no like (concat('%',#{tech_order_no},'%'))
and d.order_no=#{order_no}
and d.factory=#{factory}
and instr(d.tech_list,#{workshop})>0
and t.tech_date>=#{tech_date_start}
and t.tech_date<=#{tech_date_end}
and t.edit_date !='' and t.assign_date=''
and t.assign_date !='' and t.assess_date=''
and t.assess_date !='' and t.finish_date=''
and t.finish_date !=''
and not exists (SELECT distinct h.ecn_task_id tech_task_id FROM BMS_HR_STAFF_TECH_HOURS h WHERE 1=1
and h.factory=#{factory}
and h.workshop=#{workshop}
and h.work_hour>0 and d.id=h.ecn_task_id)
and exists (SELECT distinct h.ecn_task_id tech_task_id FROM BMS_HR_STAFF_TECH_HOURS h WHERE h.status='0'
and h.factory=#{factory}
and h.workshop=#{workshop}
and h.work_hour>0 and d.id=h.ecn_task_id)
and exists (SELECT distinct h.ecn_task_id tech_task_id FROM BMS_HR_STAFF_TECH_HOURS h WHERE h.status='1'
and h.factory=#{factory}
and h.workshop=#{workshop}
and h.work_hour>0 and d.id=h.ecn_task_id)
and exists (SELECT distinct h.ecn_task_id tech_task_id FROM BMS_HR_STAFF_TECH_HOURS h WHERE h.status='2'
and h.factory=#{factory}
and h.workshop=#{workshop}
and h.work_hour>0 and d.id=h.ecn_task_id)
order by t.tech_date desc
LIMIT #{start} ,#{length}

