SELECT * FROM ( SELECT d.id as task_detail_id, d.status_list, t.task_content, t.tech_order_type, t.tech_order_no, t.tech_date, t.switch_mode, t.switch_node,d.time_list, d.tech_task_id, d.factory, d.order_no, k.key_name AS tech_order_type_name, substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',1) as ws, substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',-1) AS total, IF(substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',1) = '自制件' or substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',1) = '部件', IFNULL(sum(f.follow_num),0),count(f1.confirmor_id) ) as complete FROM BMS_TECH_TASK_DETAIL AS d join mysql.help_topic b ON b.help_topic_id < (LENGTH(d.tech_list) - LENGTH(REPLACE(d.tech_list, ',', '')) + 1) LEFT JOIN BMS_TECH_TASK AS t ON d.tech_task_id = t.id LEFT JOIN BMS_TECH_TASK_FOLLOW_PRE f ON d.id = f.task_detail_id and substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',1) = f.workshop LEFT JOIN BMS_TECH_TASK_FOLLOW f1 ON f1.factory = d.factory AND f1.order_no = d.order_no AND f1.tech_task_id = d.tech_task_id and substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',1) = f1.workshop LEFT JOIN BMS_BASE_KEY k ON t.tech_order_type = k.id WHERE d.factory = #{factory}
AND substring_index(substring_index(substring_index(d.tech_list,',', b.help_topic_id + 1), ',', -1),':',1) = #{workshop}
GROUP BY d.tech_task_id,d.order_no,d.factory,ws ) AS tmp where 1=1
and tmp.order_no = #{order_no}
and tmp.task_content like (concat('%',#{task_content},'%'))
and tmp.tech_order_no like (concat('%',#{tech_order_no},'%'))
and tmp.tech_date>=#{tech_date_start}
and tmp.tech_date<=#{tech_date_end}
and instr(tmp.status_list,#{workshop})>0
and instr(tmp.status_list,#{workshop})<0
LIMIT #{start} ,#{length}

