mapper.select:
select o.id,o.order_name name,o.order_no orderNo,o.order_code orderCode,o.order_qty orderQty,t.internal_name busType,t.id bus_type_id from BMS_OR_ORDER o left join BMS_BASE_BUS_TYPE t on o.bus_type_id=t.id
left join BMS_OR_FACTORY_ORDER fo on fo.order_id=o.id left join BMS_BASE_FACTORY f on fo.factory_id=f.id
where o.order_no like CONCAT('%',#{orderNo},'%')
and t.internal_name=#{busType}
and f.factory_name=#{factory}
order by o.id desc

SELECT * FROM BMS_BASE_PROCESS 
WHERE 1=1 

AND id = #{id}


AND factory = #{factory}


AND workshop = #{workshop}


AND line = #{line}

select CONCAT(t.id,'') id,r.staff_number,t.factory_code,p.task_type,sum(t.count) count, sum(t.finish_count) finish_count,p.url,t.param from BMS_BASE_TASK t left join BMS_BASE_TASK_TYPE p on t.task_type_id=p.id left join BMS_BASE_USER_ROLE r on t.role_id=r.role_id where t.count!=t.finish_count
and r.staff_number=#{staff_number} and r.permission_key='1'
and p.task_type=#{task_type}
and t.param=#{param}
group by r.staff_number,t.factory_code,p.task_type,p.url,t.param

mapper.update:
update BMS_BASE_TASK set
 
	count=CAST(count AS SIGNED)+CAST(#{count} AS SIGNED),
	editor_id=#{editorId},edit_date=#{editDate}
 
 
	finish_count=CAST(finish_count AS SIGNED)+CAST(#{finish_count} AS SIGNED),
	handler=#{handler},finish_date=#{finish_date}
 
where 1=1 and count!=finish_count
 
	and task_type_id=(select id from BMS_BASE_TASK_TYPE t where t.task_type=#{task_type_name})

 
	and id=${id}
